import React from 'react';
import { findLanguageByKey } from '../../languages/Languages';
import { Code, BreakLine } from '../../styles/styles';

const SunriseSunsetInfo = ({ actualLanguage, lat, lon, sunrise, sunset }) => {
	const { find } = require('geo-tz');
	const timeZone = find(lat, lon)[0];
	const fullLanguage = findLanguageByKey(actualLanguage);
	toLocalCityTime();

	const toLocalCityTime = () => {
		const sunrise = new Date(sunrise * 1000).toLocaleString([], {
			timeStyle: 'short',
			timeZone: timeZone,
		});

		const sunset = new Date(sunset * 1000).toLocaleString([], {
			timeStyle: 'short',
			timeZone: timeZone,
		});
		return { sunriseTime, sunsetTime };
	};

	return (
		<div>
			<Code>
				{fullLanguage.words.sunrise} {sunriseTime}
			</Code>
			<BreakLine />
			<Code>
				{fullLanguage.words.sunset} {sunsetTime}
			</Code>
		</div>
	);
};

export default SunriseSunsetInfo;
