import React, { useState } from 'react';
import {
	GlobalStyle,
	WeatherCard,
	MiInfo,
	SocialNetworkName,
	SocialNetworkItem,
	NetworkContainer,
	NetworkMapContainer,
	MiInfoContainer,
	BackContainer,
	BackIcon,
} from '../../styles/styles';
import { findLanguageByKey } from '../../languages/Languages';
import { Code, BreakLine } from '../../styles/styles';

const SunriseSunsetInfo = ({ actualLanguage, timezone, sunrise, sunset }) => {
	const fullLanguage = findLanguageByKey(actualLanguage);
	toLocalCityTime();

	const toLocalCityTime = () => {
		let date = new Date();
		let utc = date.getTime() + date.getTimezoneOffset() * 60000;
		let nd = new Date(utc + 3600000 * -3);
		console.log(nd.toLocaleString());

		console.log(
			new Date(
				date.toLocaleString({
					timeZone: timezone,
				})
			)
		);
	};

	return (
		<div>
			<Code>
				{fullLanguage.words.sunrise} {sunrise}
			</Code>
			<BreakLine />
			<Code>
				{fullLanguage.words.sunset} {sunset}
			</Code>
		</div>
	);
};

export default SunriseSunsetInfo;
